  // Create contact
  createContact: function(contact) {
    return function(dispatch) {
      return ContactApi
        .createContact(contact)
        .then(contact => {
          dispatch(actionsFunction.createContactSuccess(contact));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  createContactSuccess: function(contact) {
    return { type: types.CREATE_CONTACT_SUCCESS, payload: contact };
  },


  // Delete contact
  deleteContact: function(id) {
    return function(dispatch) {
      return ContactApi
        .deleteContact(id)
        .then(contact => {
          dispatch(actionsFunction.deleteContactSuccess(contact));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  deleteContactSuccess: function(contact) {
    return { type: types.DELETE_CONTACT_SUCCESS, payload: contact };
  },


  // Get contact
  loadContact: function(id) {
    return function(dispatch) {
      return ContactApi
        .getOneContact(id)
        .then(contact => {
          dispatch(actionsFunction.loadContactSuccess(contact));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadContactSuccess: function(contact) {
    return { type: types.GET_CONTACT_SUCCESS, payload: contact };
  },

  // Load  list
  loadContactList: function() {
    return function(dispatch) {
      return ContactApi
        .getContactList()
        .then(list => {
          dispatch(actionsFunction.loadContactListSuccess(list));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadContactListSuccess: function(list) {
    return { type: types.LIST_CONTACT_SUCCESS, payload: list };
  },

	
  // Save contact
